mapper.select:
SELECT wu1.*,sd.`name` deptName from(
    SELECT * FROM `wx_user` wu


            and wu.nick_name like  concat('%',#{nickName},'%')


            and wu.phone like concat('%',#{phone},'%')


            and dept_id in

                #{deptId}



            and sc.create_time between #{createBeginTime} and #{createEndTime}




    limit #{currRecord},#{pageSize}

) wu1
left JOIN sys_dept sd ON sd.dept_id = wu1.dept_id

SELECT wu.*,sd.`name` deptName FROM `wx_user` wu
left JOIN sys_dept sd ON sd.dept_id = wu.dept_id
where wu.id = #{wxUserId}

SELECT wu1.id, wu1.deptName, wu1.num, CASE WHEN @prevRank = wu1.num THEN @curRank WHEN @prevRank := wu1.num THEN @curRank := @curRank + 1 END AS rank FROM ( SELECT count(id) as num,wu.dept_id,wu.id,sd.name as deptName FROM wx_user wu LEFT JOIN sys_dept sd ON sd.dept_id = wu.dept_id
and dept_id in
#{deptId}
and sc.create_time between #{createBeginTime} and #{createEndTime}
AND wu.create_date IS NOT NULL GROUP BY dept_id ) wu1, (SELECT @curRank :=0, @prevRank := NULL) r ORDER BY wu1.num desc
